    private void attackCygnusActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_attackCygnusActionPerformed
        cygnusText.setText(pet.fightCygnus());
        if(pet.getDead()==true)
        {
            continueCygnus.setVisible(true);
            attackCygnus.setEnabled(false);
            fleeCygnus.setEnabled(false);
            cygnusText.setText(pet.getName()+" fainted...");
        }
        if(pet.getCygnusDead()==true)
        {
            continueCygnus.setVisible(true);
            attackCygnus.setEnabled(false);
            fleeCygnus.setEnabled(false);
            if(pet.getMilkyTwo()==false)
                cygnusText.setText(cygnusText.getText()+"\nCongratulations! You defeated the Cygnus alien\nleader. You have now unlocked level two in the Milky\nWay galaxy!");
            else
                cygnusText.setText(cygnusText.getText()+"\nCongratulations! You defeated the Cygnus\nalien leader.");
            pet.setMilkyTwo(true);
            twoMilky.setEnabled(true);
            resetMilkyOne=true;
            pet.setMoney(pet.getMoney()+100);
        }
        statBars();
    }//GEN-LAST:event_attackCygnusActionPerformed

    private void fleeCygnusActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_fleeCygnusActionPerformed
        String s=pet.fleeCygnus();
        if(s=="You managed to get away safely.")
        {
            continueCygnus.setVisible(true);
            attackCygnus.setEnabled(false);
            fleeCygnus.setEnabled(false);
            resetMilkyOne=true;
        }
        cygnusText.setText(s);
        statBars();
    }//GEN-LAST:event_fleeCygnusActionPerformed

