    private void nextRiddleActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_nextRiddleActionPerformed
        if(riddleCount==1)
        {
            perseusText.setText(pet.perseusRiddleOne());
        }
        if(riddleCount==2)
        {
            perseusText.setText(pet.perseusRiddleTwo());
        }
        if(riddleCount==3)
        {
            perseusText.setText(pet.perseusRiddleThree());
        }
        if(riddleCount>3)
        {
            perseusText.setText("Congratulations! You have answered all three riddles\ncorrectly and retrieved the Orion data files. You have\nnow unlocked level three in the Milky Way Galaxy!");
            pet.setMilkyThree(true);
            threeMilky.setEnabled(true);
            submitAnswer.setEnabled(false);
            nextRiddle.setEnabled(false);
            continuePerseus.setVisible(true);
        }
    }//GEN-LAST:event_nextRiddleActionPerformed

    private void submitAnswerActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_submitAnswerActionPerformed
        if(riddleCount==1&&riddleAnswer.getText().contains(pet.perseusAnswerOne()))
        {
            riddleCount++;
            perseusText.setText("Correct!");
        }
        if(riddleCount==2&&riddleAnswer.getText().equals(pet.perseusAnswerTwo()))
        {
            riddleCount++;
            perseusText.setText("Correct!");
        }
        if(riddleCount==3&&riddleAnswer.getText().equals(pet.perseusAnswerThree()))
        {
            perseusText.setText("Correct!");
            riddleCount++;
        }
        if(!riddleAnswer.getText().contains(pet.perseusAnswerOne())&&!riddleAnswer.getText().equals(pet.perseusAnswerTwo())&&!riddleAnswer.getText().equals(pet.perseusAnswerThree()))
        {
            strikeCount++;
            perseusX();
        }
    }//GEN-LAST:event_submitAnswerActionPerformed

