    private void fightMitnakaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_fightMitnakaActionPerformed
        mitnakaText.setText(pet.fightMitnaka());
        if(pet.getDead()==true)
        {
            continueMitnaka.setVisible(true);
            fightMitnaka.setEnabled(false);
            confuseMitnaka.setEnabled(false);
            resetMilkyThree=true;
            mitnakaText.setText(mitnakaText.getText()+"\n"+pet.getName()+" fainted...");
        }
        if(pet.getMitnakaDead()==true)
        {
            continueMitnaka.setVisible(true);
            fightMitnaka.setEnabled(false);
            confuseMitnaka.setEnabled(false);
            if(pet.getMilkyFour()==false)
                mitnakaText.setText(mitnakaText.getText()+"\nCongratulations! You have completed the heist and returned the diamonds to NASA Headquarters.\nYou have now unlocked level four in the Milky Way Galaxy.");
            else
                mitnakaText.setText(mitnakaText.getText()+"\nCongratulations! You have completed the heist and returned the diamonds to NASA Headquarters.");
            pet.setMilkyFour(true);
            fourMilky.setEnabled(true);
            resetMilkyThree=true;
            pet.setMoney(pet.getMoney()+200);
        }
        statBars();
    }//GEN-LAST:event_fightMitnakaActionPerformed

