    private void tigerFightActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_tigerFightActionPerformed
        if(pet.getNeilDead()==true)
        {
            galacticCenterText.setText("");
        }
        tigerFight.setEnabled(false);
        tigerNurse.setEnabled(false);
        neilFight.setEnabled(true);
        neilNurse.setEnabled(true);
        galacticCenterText.setText(pet.tigerMove(true));
        if(pet.getNeilDead()==true)
        {
            tigerFight.setEnabled(true);
            tigerNurse.setEnabled(true);
            neilFight.setEnabled(false);
            neilNurse.setEnabled(false);
        }
        if(pet.getLeopardDead()==true)
        {
            tigerFight.setEnabled(false);
            tigerNurse.setEnabled(false);
            neilFight.setEnabled(false);
            neilNurse.setEnabled(false);
            galacticCenterText.setText(galacticCenterText.getText()+"\n\nAwesome job! You defeated the leopard kingdom and\nconquered the Milky Way Galaxy!");
            continueCenter.setVisible(true);
        }
        if(pet.getDead()==true)
        {
            tigerFight.setEnabled(false);
            tigerNurse.setEnabled(false);
            neilFight.setEnabled(false);
            neilNurse.setEnabled(false);
            galacticCenterText.setText(galacticCenterText.getText()+"\n\nThe leopard won the fight...\nNASA teleported you guys back home,\nhopefully you're more prepared for next time");
            continueCenter.setVisible(true);
        }
        statBars();
    }//GEN-LAST:event_tigerFightActionPerformed

