    private void continueCenterActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_continueCenterActionPerformed
        if(pet.getLeopardDead()==true)
        {
            milkyWayFive.setVisible(false);
            gameOverPanel.setVisible(true);
            deathMoneyAmount.setText("$1000000000");
            deathCause.setVisible(false);
            causeOfDeath.setVisible(false);
            gameWon.setVisible(true);
            deathPic.setVisible(false);
            kingPic.setVisible(true);
        }
        if(pet.getLeopardDead()==false)
        {
            milkyWayFive.setVisible(false);
            gamePlayPanel.setVisible(true);
            continueCenter.setVisible(false);
            pet.setLeopardDead(false);
            pet.setNeilDead(false);
            pet.setDead(false);
            pet.setHunger(pet.getHunger()+50);
            pet.setNeilHealth(100);
            pet.setLeopardHealth(100);
            pet.setLeopardMedicine(3);
            tigerFight.setEnabled(true);
            tigerNurse.setEnabled(true);
            neilFight.setEnabled(false);
            neilFight.setEnabled(false);
            galacticCenterText.setText("");
            noSayPet=false;
            noSayNeil=false;
            statBars();
        }
    }//GEN-LAST:event_continueCenterActionPerformed
    private void statBars()
    {
        hungerBar.setValue(pet.getHunger());
        ageBar.setValue(pet.getAge());
        strengthBar.setValue(pet.getStrength());
        healthBar.setValue(pet.getHealth());
        moneyAmount.setText("$"+pet.getMoney());
        foodAmount.setText(pet.getFood()+" cookies");
        medicineAmount.setText(pet.getMedicine()+" pills");
        cygnusAlienHealthBar.setValue(pet.getCygnusHealth());
        cygnusHealthBar.setValue(pet.getHealth());
        mitnakaHealthBar.setValue(pet.getHealth());
        mitnakaAlienHealthBar.setValue(pet.getMitnakaHealth());
        tigerHealthBar.setValue(pet.getHealth());
        neilHealthBar.setValue(pet.getNeilHealth());
        leopardHealthBar.setValue(pet.getLeopardHealth());
        pillAmount.setText(pet.getMedicine()+" pills");
        if(pet.getGameOver()==true)
        {
            gamePlayPanel.setVisible(false);
            gameOverPanel.setVisible(true);
        }
    }
    private void perseusX()
    {
        if(strikeCount==1)
            perseusX1.setVisible(true);
        if(strikeCount==2)
            perseusX2.setVisible(true);
        if(strikeCount==3)
        {
            perseusX3.setVisible(true);
            perseusText.setText("You have failed the mission, now "+pet.getName()+" has to try\nto fight your way out of Perseus.");
            if(pet.getStrength()<20)
            {
                perseusText.setText(perseusText.getText()+"\n"+pet.getName()+" was too weak. Fortunately, NASA teleported\nyour fainted bodies back to earth before it was too late.");
                pet.setHealth(0);
            }
            else
            {
                perseusText.setText(perseusText.getText()+"\n"+pet.getName()+" overpowered the Perseuns, you were able to escape to your ship.");
            }
            continuePerseus.setVisible(true);
            submitAnswer.setEnabled(false);
            nextRiddle.setEnabled(false);
            pet.setMoney(pet.getMoney()-150);
        }
        statBars();
    }
